function abreOuFechaResposta(e){let t=e.target.parentNode;t.classList.toggle("faq__questions__item--is-open")}function removeBotaoAtivo(){let e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].classList.remove("shows__tabs__button--is-active")}function escondeTodasAsAbas(){let e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("shows__list--is-active")}function ocultaElementosDoHeader(){let e=document.querySelector("header");e.classList.add("header--is-hidden")}function exibeElementosDoHeader(){let e=document.querySelector("header");e.classList.remove("header--is-hidden")}document.addEventListener("DOMContentLoaded",function(){let e=document.querySelectorAll("[data-tab-button]"),t=document.querySelectorAll("[data-faq-question]"),a=document.querySelector(".hero"),o=a.clientHeight;window.addEventListener("scroll",function(){let e=window.scrollY;e>o?ocultaElementosDoHeader():exibeElementosDoHeader()});for(let s=0;s<e.length;s++)e[s].addEventListener("click",function(e){let t=e.target.dataset.tabButton,a=document.querySelector(`[data-tab-id=${t}]`);escondeTodasAsAbas(),a.classList.add("shows__list--is-active"),removeBotaoAtivo(),e.target.classList.add("shows__tabs__button--is-active")});for(let l=0;l<t.length;l++)t[l].addEventListener("click",abreOuFechaResposta)});